import { Component, VERSION } from '@angular/core';
import { map, Observable, timer } from 'rxjs';

@Component({
  selector: 'app-counter-component',
  templateUrl: './counter.component.html'
})
export class CounterComponent {
  public currentCount = 0;

  public incrementCounter() {
    this.currentCount++;
  }

  public name = 'Angular ' + VERSION.major;

  timer$: Observable<{ index: number; randomValue: number }>;

  constructor() {
    this.timer$ = timer(0, 1000).pipe(
      map((num: number) => {
        return {
          index: num,
          randomValue: Math.random()
        };
      })
    );
  }
}
<h1>Counter</h1>

<p>This is a simple example of an Angular component.</p>

<p aria-live="polite">Current count: <strong>{{ currentCount }}</strong></p>

<button class="btn btn-primary" (click)="incrementCounter()">Increment</button>

<br />

<p>Use directive for permission check</p>

<div *hasPermission="[1,2,3]">
  this element has permission
</div>


<div *hasPermission="[1,3,5]">
  this element does not have permission
</div>


<br/>
<p>Use ngrxLet instead of ngIf</p>
<div *ngrxLet="timer$; let randomNumbers; let e = $error, let c = $complete">
  index: {{ randomNumbers.index }}<br />
  randomValue: {{ randomNumbers.randomValue }}<br />
  randomValue again:{{ randomNumbers.randomValue }}
  <div *ngIf="e">
    error: {{e}}
  </div>
  <div *ngIf="c">
    completed: {{c}}
  </div>
</div>
